body.raw:
{
  "params": [
    {
      "name": "title",
      "type": "string",
      "description": "Título da atividade"
    },
    {
      "name": "grade",
      "type": "integer",
      "description": "Ano (10,11,12)"
    },
    {
      "name": "modules",
      "type": "array",
      "items": "string",
      "description": "Módulos temáticos"
    },
    {
      "name": "number_of_exercises",
      "type": "integer"
    },
    {
      "name": "total_time_minutes",
      "type": "integer"
    },
    {
      "name": "number_of_retries",
      "type": "integer"
    },
    {
      "name": "relative_tolerance_pct",
      "type": "number"
    },
    {
      "name": "absolute_tolerance",
      "type": "number"
    },
    {
      "name": "show_answers_after_submission",
      "type": "boolean"
    },
    {
      "name": "scoring_policy",
      "type": "string",
      "enum": [
        "linear",
        "non-linear"
      ]
    },
    {
      "name": "approval_threshold",
      "type": "number"
    },
    {
      "name": "exercises",
      "type": "array",
      "items": "object",
      "description": "Lista de exercícios (question, options, correct_options, correct_answer)"
    }
  ]
}
